import React from "react";
import styled from "styled-components";
import {
  Specialsubtitle1,
  Specialsubtitle2,
  Subtitle2,
} from "../../atoms/text";
import { COLOR } from "../../constants";

const WordDetail = ({ num, engWord, korWord, diacritic }) => {
  return (
    <Container>
      <Specialsubtitle1 color={COLOR.BRANDCOLOR} padding={"1rem"}>
        {num}
      </Specialsubtitle1>
      <Container>
        <>
          <Specialsubtitle1 color={COLOR.WHITE}>{engWord}</Specialsubtitle1>
          <Specialsubtitle2 color={COLOR.GRAY500} padding={"1rem"}>
            {diacritic}
          </Specialsubtitle2>
        </>
        <Subtitle2 color={COLOR.GRAY100}>{korWord}</Subtitle2>
      </Container>
    </Container>
  );
};

export default WordDetail;

const Container = styled.div`
  display: flex;
  height: 5rem;
  width: 100%;
  flex-direction: row;
  margin: 0rem 2rem 2rem 3rem;
  aligh-items: center;
  justify-content: space-between;
  background-color: #454a55;
  border-radius: 1.25rem;
`;
